import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import AppointmentForm from './components/AppointmentForm';
import ReservationTable from './components/ReservationTable';
import { AppointmentData } from './components/AppointmentForm';

function App() {
  const [reservations, setReservations] = useState<AppointmentData[]>([]);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [editingData, setEditingData] = useState<AppointmentData | null>(null);

  useEffect(() => {
    fetch('http://localhost:4000/api/reservations')
      .then(response => response.json())
      .then(data => setReservations(data))
      .catch(error => console.error('Error fetching reservations:', error));
  }, []);

  const handleAppointmentSubmit = (formData: AppointmentData) => {
    if (editingIndex !== null && editingData) {
      // Update existing reservation
      const updatedReservations = [...reservations];
      updatedReservations[editingIndex] = formData;
      setReservations(updatedReservations);
      setEditingIndex(null);
      setEditingData(null);
      alert('예약이 성공적으로 업데이트되었습니다!');
    } else {
      // Add new reservation
      axios.post('http://localhost:4000/api/reservations', formData)
        .then(response => {
          setReservations([...reservations, response.data]);
          alert('예약이 성공적으로 완료되었습니다!');
        })
        .catch(error => {
          console.error('Error creating reservation:', error);
          alert('예약에 실패했습니다. 다시 시도해주세요.');
        });
    }
  };

  const handleEdit = (reservation: AppointmentData, index: number) => {
    setEditingIndex(index);
    setEditingData(reservation);
    // Scroll to the form
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleDelete = (index: number) => {
    if (window.confirm('이 예약을 삭제하시겠습니까?')) {
      const updatedReservations = [...reservations];
      updatedReservations.splice(index, 1);
      setReservations(updatedReservations);

      // If we're currently editing this reservation, reset the editing state
      if (editingIndex === index) {
        setEditingIndex(null);
        setEditingData(null);
      } else if (editingIndex !== null && editingIndex > index) {
        // Adjust the editing index if we're deleting a reservation before the one being edited
        setEditingIndex(editingIndex - 1);
      }
    }
  };

  const handleCancelEdit = () => {
    setEditingIndex(null);
    setEditingData(null);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1 className="text-3xl font-bold mb-6">Hair Salon Appointment</h1>
      </header>
      <main className="py-8">
        <AppointmentForm 
          onSubmit={handleAppointmentSubmit} 
          initialData={editingData || undefined}
          onCancelEdit={editingData ? handleCancelEdit : undefined}
        />

        <div className="mt-12">
          <ReservationTable 
            reservations={reservations} 
            onEdit={handleEdit} 
            onDelete={handleDelete} 
          />
        </div>
      </main>
    </div>
  );
}

export default App;
