import React, { useState } from 'react';
import { AppointmentData } from './AppointmentForm';

interface ReservationTableProps {
  reservations: AppointmentData[];
  onEdit: (reservation: AppointmentData, index: number) => void;
  onDelete: (index: number) => void;
}

const ReservationTable: React.FC<ReservationTableProps> = ({ 
  reservations, 
  onEdit, 
  onDelete 
}) => {
  return (
    <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">예약 목록</h2>

      {reservations.length === 0 ? (
        <p className="text-gray-500 text-center py-4">예약이 없습니다.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border border-gray-200">
            <thead>
              <tr className="bg-gray-100">
                <th className="py-2 px-4 border-b text-left">고객 이름</th>
                <th className="py-2 px-4 border-b text-left">날짜</th>
                <th className="py-2 px-4 border-b text-left">시간</th>
                <th className="py-2 px-4 border-b text-left">스타일리스트</th>
                <th className="py-2 px-4 border-b text-left">서비스</th>
                <th className="py-2 px-4 border-b text-left">작업</th>
              </tr>
            </thead>
            <tbody>
              {reservations.map((reservation, index) => (
                <tr key={index} className="hover:bg-gray-50">
                  <td className="py-2 px-4 border-b">{reservation.customerName}</td>
                  <td className="py-2 px-4 border-b">{reservation.date}</td>
                  <td className="py-2 px-4 border-b">{reservation.time}</td>
                  <td className="py-2 px-4 border-b">{reservation.stylist}</td>
                  <td className="py-2 px-4 border-b">{reservation.serviceType}</td>
                  <td className="py-2 px-4 border-b">
                    <div className="flex space-x-2">
                      <button
                        onClick={() => onEdit(reservation, index)}
                        className="bg-blue-500 hover:bg-blue-600 text-white py-1 px-3 rounded-md text-sm transition duration-300"
                      >
                        수정
                      </button>
                      <button
                        onClick={() => onDelete(index)}
                        className="bg-red-500 hover:bg-red-600 text-white py-1 px-3 rounded-md text-sm transition duration-300"
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ReservationTable;
