import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import AppointmentForm from './components/AppointmentForm';
import ReservationTable from './components/ReservationTable';
import { AppointmentData } from './components/AppointmentForm';

interface ToastMessage {
  id: string;
  message: string;
  type: 'success' | 'error' | 'warning' | 'info';
}

function App() {
  const [reservations, setReservations] = useState<AppointmentData[]>([]);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [editingData, setEditingData] = useState<AppointmentData | null>(null);
  const [toasts, setToasts] = useState<ToastMessage[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  // Toast message functions
  const addToast = (message: string, type: ToastMessage['type'] = 'info') => {
    const id = Math.random().toString(36).substr(2, 9);
    const newToast: ToastMessage = { id, message, type };
    setToasts(prev => [...prev, newToast]);

    // Auto-remove toast after 5 seconds
    setTimeout(() => {
      setToasts(prev => prev.filter(toast => toast.id !== id));
    }, 5000);
  };

  const removeToast = (id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  useEffect(() => {
    setIsLoading(true);
    axios.get('http://localhost:3000/api/reservations')
      .then(response => {
        setReservations(response.data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error fetching reservations:', error);
        addToast('ÏòàÏïΩ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.', 'error');
        setIsLoading(false);
      });
  }, []);

  const handleAppointmentSubmit = async (formData: AppointmentData) => {
    try {
      if (editingIndex !== null && editingData) {
        // Update existing reservation via API
        const response = await axios.put(`http://localhost:3000/api/reservations/${editingData._id}`, formData);
        const updatedReservations = [...reservations];
        updatedReservations[editingIndex] = response.data;
        setReservations(updatedReservations);
        setEditingIndex(null);
        setEditingData(null);
        addToast('ÏòàÏïΩÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§!', 'success');
      } else {
        // Add new reservation
        const response = await axios.post('http://localhost:3000/api/reservations', formData);
        setReservations([...reservations, response.data]);
        addToast('ÏòàÏïΩÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!', 'success');
      }
    } catch (error: any) {
      console.error('Error with reservation:', error);

      if (error.response) {
        // Server responded with error status
        const statusCode = error.response.status;
        const errorMessage = error.response.data?.error || error.response.data?.message;

        if (statusCode === 400) {
          addToast(`ÏûÖÎ†• Ïò§Î•ò: ${errorMessage}`, 'error');
        } else if (statusCode === 404) {
          addToast('ÏòàÏïΩÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.', 'error');
        } else if (statusCode >= 500) {
          addToast('ÏÑúÎ≤Ñ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§. Ïû†Ïãú ÌõÑ Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.', 'error');
        } else {
          addToast('ÏòàÏïΩ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.', 'error');
        }
      } else if (error.request) {
        // Network error
        addToast('ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.', 'error');
      } else {
        // Other error
        addToast('ÏòàÏÉÅÏπò Î™ªÌïú Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.', 'error');
      }
    }
  };

  const handleEdit = (reservation: AppointmentData, index: number) => {
    setEditingIndex(index);
    setEditingData(reservation);
    // Scroll to the form
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleDelete = async (index: number) => {
    const reservation = reservations[index];

    if (!reservation._id) {
      addToast('ÏòàÏïΩ IDÍ∞Ä ÏóÜÏñ¥ ÏÇ≠Ï†úÌï† Ïàò ÏóÜÏäµÎãàÎã§.', 'error');
      return;
    }

    if (window.confirm(`${reservation.customerName}ÎãòÏùò ${reservation.date} ${reservation.time} ÏòàÏïΩÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?`)) {
      try {
        await axios.delete(`http://localhost:3000/api/reservations/${reservation._id}`);

        const updatedReservations = [...reservations];
        updatedReservations.splice(index, 1);
        setReservations(updatedReservations);

        // If we're currently editing this reservation, reset the editing state
        if (editingIndex === index) {
          setEditingIndex(null);
          setEditingData(null);
        } else if (editingIndex !== null && editingIndex > index) {
          // Adjust the editing index if we're deleting a reservation before the one being edited
          setEditingIndex(editingIndex - 1);
        }

        addToast(`${reservation.customerName}ÎãòÏùò ÏòàÏïΩÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.`, 'success');
      } catch (error: any) {
        console.error('Error deleting reservation:', error);

        if (error.response?.status === 404) {
          addToast('ÏÇ≠Ï†úÌïòÎ†§Îäî ÏòàÏïΩÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.', 'error');
        } else if (error.request) {
          addToast('ÎÑ§Ìä∏ÏõåÌÅ¨ Ïó∞Í≤∞ÏùÑ ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.', 'error');
        } else {
          addToast('ÏòàÏïΩ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.', 'error');
        }
      }
    }
  };

  const handleCancelEdit = () => {
    setEditingIndex(null);
    setEditingData(null);
  };

  return (
    <div className="App">
      <header className="App-header text-center">
        <h1>üíá‚Äç‚ôÄÔ∏è Ìó§Ïñ¥ ÏÇ¥Î°± ÏòàÏïΩ ÏãúÏä§ÌÖú</h1>
      </header>
      <main className="py-8">
        <AppointmentForm 
          onSubmit={handleAppointmentSubmit} 
          initialData={editingData || undefined}
          onCancelEdit={editingData ? handleCancelEdit : undefined}
        />

        <div className="mt-12">
          {isLoading ? (
            <div className="max-w-4xl mx-auto bg-white p-8 rounded-lg shadow-lg">
              <div className="flex items-center justify-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
                <span className="ml-3 text-gray-600">ÏòàÏïΩ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë...</span>
              </div>
            </div>
          ) : (
            <ReservationTable 
              reservations={reservations} 
              onEdit={handleEdit} 
              onDelete={handleDelete} 
            />
          )}
        </div>
      </main>

      {/* Toast Messages */}
      <div className="fixed bottom-4 right-4 space-y-2 z-50">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`max-w-sm w-full bg-white border-l-4 p-4 rounded-md shadow-lg transition-all duration-300 ${
              toast.type === 'success' ? 'border-green-500' :
              toast.type === 'error' ? 'border-red-500' :
              toast.type === 'warning' ? 'border-yellow-500' :
              'border-blue-500'
            }`}
            role="alert"
            aria-live="polite"
          >
            <div className="flex items-center justify-between">
              <div className="flex items-center">
                <div className={`mr-3 ${
                  toast.type === 'success' ? 'text-green-500' :
                  toast.type === 'error' ? 'text-red-500' :
                  toast.type === 'warning' ? 'text-yellow-500' :
                  'text-blue-500'
                }`}>
                  {toast.type === 'success' && '‚úì'}
                  {toast.type === 'error' && '‚úó'}
                  {toast.type === 'warning' && '‚ö†'}
                  {toast.type === 'info' && '‚Ñπ'}
                </div>
                <p className="text-sm font-medium text-gray-900">{toast.message}</p>
              </div>
              <button
                onClick={() => removeToast(toast.id)}
                className="ml-3 text-gray-400 hover:text-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 rounded"
                aria-label="Îã´Í∏∞"
              >
                <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
