import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';
import AppointmentForm from './components/AppointmentForm';
import ReservationTable from './components/ReservationTable';
import { AppointmentData } from './components/AppointmentForm';

function App() {
  const [reservations, setReservations] = useState<AppointmentData[]>([]);
  const [editingIndex, setEditingIndex] = useState<number | null>(null);
  const [editingData, setEditingData] = useState<AppointmentData | null>(null);

  useEffect(() => {
    axios.get('http://localhost:4000/api/reservations')
      .then(response => setReservations(response.data))
      .catch(error => console.error('Error fetching reservations:', error));
  }, []);

  const handleAppointmentSubmit = (formData: AppointmentData) => {
    if (editingIndex !== null && editingData) {
      // Update existing reservation (keeping it local as per requirements)
      const updatedReservations = [...reservations];
      // Preserve the _id from the original reservation
      updatedReservations[editingIndex] = {
        ...formData,
        _id: editingData._id // Keep the original _id
      };
      setReservations(updatedReservations);
      setEditingIndex(null);
      setEditingData(null);
      alert('ÏòàÏïΩÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏ÎêòÏóàÏäµÎãàÎã§!');
    } else {
      // Add new reservation
      axios.post('http://localhost:4000/api/reservations', formData)
        .then(response => {
          setReservations([...reservations, response.data]);
          alert('ÏòàÏïΩÏù¥ ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!');
        })
        .catch(error => {
          console.error('Error creating reservation:', error);
          alert('ÏòàÏïΩÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
        });
    }
  };

  const handleEdit = (reservation: AppointmentData, index: number) => {
    setEditingIndex(index);
    setEditingData(reservation);
    // Scroll to the form
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleDelete = (index: number) => {
    if (window.confirm('Ïù¥ ÏòàÏïΩÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      const reservation = reservations[index];

      if (!reservation._id) {
        console.error('Cannot delete reservation without ID');
        alert('ÏòàÏïΩ IDÍ∞Ä ÏóÜÏñ¥ ÏÇ≠Ï†úÌï† Ïàò ÏóÜÏäµÎãàÎã§.');
        return;
      }

      axios.delete(`http://localhost:4000/api/reservations/${reservation._id}`)
        .then(() => {
          const updatedReservations = [...reservations];
          updatedReservations.splice(index, 1);
          setReservations(updatedReservations);

          // If we're currently editing this reservation, reset the editing state
          if (editingIndex === index) {
            setEditingIndex(null);
            setEditingData(null);
          } else if (editingIndex !== null && editingIndex > index) {
            // Adjust the editing index if we're deleting a reservation before the one being edited
            setEditingIndex(editingIndex - 1);
          }
        })
        .catch(error => {
          console.error('Error deleting reservation:', error);
          alert('ÏòàÏïΩ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. Îã§Ïãú ÏãúÎèÑÌï¥Ï£ºÏÑ∏Ïöî.');
        });
    }
  };

  const handleCancelEdit = () => {
    setEditingIndex(null);
    setEditingData(null);
  };

  return (
    <div className="App">
      <header className="App-header text-center">
        <h1>üíá‚Äç‚ôÄÔ∏è Ìó§Ïñ¥ ÏÇ¥Î°± ÏòàÏïΩ ÏãúÏä§ÌÖú</h1>
      </header>
      <main className="py-8">
        <AppointmentForm 
          onSubmit={handleAppointmentSubmit} 
          initialData={editingData || undefined}
          onCancelEdit={editingData ? handleCancelEdit : undefined}
        />

        <div className="mt-12">
          <ReservationTable 
            reservations={reservations} 
            onEdit={handleEdit} 
            onDelete={handleDelete} 
          />
        </div>
      </main>
    </div>
  );
}

export default App;
